import react, { useState } from "react";
import PageHeader from "../../../components/PageHeader";
import { makeStyles } from "@material-ui/styles";
import EAMSelect from "../../../components/EAMSelect";
import EAMText from "../../../components/EAMText";
import FormRow from "../../../components/Form/FormRow";
import { useHistory, useLocation } from "react-router";

const useStyles = makeStyles({
    container: {
        "& .MuiTimelineItem-missingOppositeContent:before": {
            padding: 0,
        },
        fieldsetContainer: {
            width: "100%",
            minHeight: "65px",
            border: "1px solid #E9EBEF",
            color: "#979DAC",
            display: "inline-block",
            borderRadius: "4px",
            fontSize: "12px",
            position: "relative",
        },
        fieldValue: {
            paddingTop: 6,
            color: "rgb(17, 17, 17)",
            fontSize: 13,
            paddingLeft: 20,
            width: "70%",
            height: "auto",
            paddingBottom: 6,
        },
    }
});

const checklistData = [
    {
        index: 1,
        id: "Warehouse",
        title: "Warehouse",
        active: true,
    },
    {
        index: 2,
        id: "ProductionOrder",
        title: "Production Order",
    },
    {
        index: 3,
        id: "ProductionWorkOrder",
        title: "Production work Order",
    },
];


export default function ApproveAnywhereChecklist() {
    const classes = useStyles();
    const history = useHistory();
    const location = useLocation();
    const [openHistory, setOpenHistory] = useState(true);
    const onFieldChange = () => {}

    const handleCloseHistory = () => {
        const loc = location.pathname;
        const newLoc = loc.replace("/checklist", "");
        history.push(newLoc);
    };

    return (
        <div className={classes.container}>
            <PageHeader title='PIS/2021/4325 | Checklist' showBack />
            <fieldset className={classnames(classes.fieldsetContainer)}>
                <legend>Checklist</legend>
                <div className={classes.fieldValue}>""</div>
                <span className={classes.rightActionWrapper}>
                    <span onClick={handleClickOpenSearch}>
                        <SearchIcon />
                    </span>
                </span>
            </fieldset>
        </div>
    );
}
