import react, { useState } from "react";
import PageHeader from "../../../components/PageHeader";
import { makeStyles } from "@material-ui/styles";
import SearchIcon from "../../../assets/Icons/SearchIcon";
import classnames from "classnames";
import ChecklistSearchList from "./ChecklistSearchList";

import { useHistory, useLocation } from "react-router";

const useStyles = makeStyles({
    container: {
        "& .MuiTimelineItem-missingOppositeContent:before": {
            padding: 0,
        },
        fieldsetContainer: {
            width: "100%",
            minHeight: "65px",
            border: "1px solid #E9EBEF",
            color: "#979DAC",
            display: "inline-block",
            borderRadius: "4px",
            fontSize: "12px",
            position: "relative",
        },
        fieldValue: {
            paddingTop: 6,
            color: "rgb(17, 17, 17)",
            fontSize: 13,
            paddingLeft: 20,
            width: "70%",
            height: "auto",
            paddingBottom: 6,
        },
        rightActionWrapper: {
            position: "absolute",
            right: 0,
            top: 10,
            "& > svg": {
                marginRight: 24,
            },
            "& > span": {
                marginRight: 24,
            },
        },
    }
});

const checklistData = [
    {
        index: 1,
        id: "Warehouse",
        title: "Warehouse",
        active: true,
    },
    {
        index: 2,
        id: "ProductionOrder",
        title: "Production Order",
    },
    {
        index: 3,
        id: "ProductionWorkOrder",
        title: "Production work Order",
    },
];


export default function ApproveAnywhereChecklistSearch() {
    const classes = useStyles();
    const [openSearch, setOpenSearch] = useState(false);


    const handleClickOpenSearch = () => {
        setOpenSearch(true);
    };


    const handleCloseChecklistSearchList = (e) => {
        setOpenSearch(false);
    };

    return (
        <div className={classes.container}>
            <PageHeader title='PIS/2021/4325 | Checklist' showBack />
            <fieldset className={classnames(classes.fieldsetContainer)}>
                <legend>Checklist</legend>
                <div className={classes.fieldValue}>""</div>
                <span className={classes.rightActionWrapper}>
                    <span onClick={handleClickOpenSearch}>
                        <SearchIcon />
                    </span>
                </span>
            </fieldset>
            <ChecklistSearchList
                open={openSearch}
                value={checklistData}
                onItemClick={handleCloseChecklistSearchList}
                onClose={e => handleCloseChecklistSearchList(e)}
            />
        </div>
    );
}
