import React, { useContext } from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import Divider from '@material-ui/core/Divider';
import { AuthContext } from '../../../utils/Providers/AuthProvider';
import { useNavigation } from "../../../app/trackedContext";
import { connect } from "react-redux";
import RamcoLogo from "../../../assets/Icons/RamcoLogo";
import { useHistory } from 'react-router-dom';
import "../../css/container/RTMenuDrawer.css";


const useStyles = makeStyles(theme => ({
  header: {
    background: '#001845',
    height: 55,
    width: '100%',
    display: 'flex',
    justifyContent: 'space-between',
    '& > div:first-child': {
      display: 'flex',
      flexDirection: 'row',
      marginLeft: 12,
    }
  },
  avator: {
    width: 28,
    height: 28,
    margin: 12,
  },
  avatorName: {
    marginTop: 12,
    fontSize: 14,
    weight: 700,
    color: '#fff'
  },
  role: {
    fontSize: 11,
    fontWeight: 600,
    fontStyle: 'italic',
    color: '#fff'
  },
  logOut: {
    marginTop: 'auto',
    marginBottom: 'auto'
  },
  rtMenuContainer: {
    background: 'linear-gradient(165.98deg, #52E5E7 0%, #130CB7 100.63%)',
    boxShadow: '0px 4px 4px rgba(0, 0, 0, 0.25)'
  },
  list: {
    width: '85vw',
    [theme.breakpoints.up("sm")]: {
      width: '50vw'
    },
    [theme.breakpoints.up("md")]: {
      width: '35vw'
    }
  },
  listItem: {
    color: "#ffffff"
  },
  fullList: {
    width: 'auto',
  },
}))

const MENU_LIST = [
  { label: 'Work Request', url: '/workRequest' },
  { label: 'Create Work Order', url: '/workOrder' },
  { label: 'Work Order Reporting', url: '/dashboard' },
  { label: 'Work Order Service Reporting', url: '/dashboard' },
  { label: 'Maintaince Inquiry', url: '/dashboard' },
  { label: 'Inspecting Order Reporting', url: '/dashboard' },
  { label: 'Stock Inquiry', url: '/warehouse' },
  { label: 'Project Tracking', url: '/projectTracking'},
  { label: 'Approve Anywhere', url: '/approveAnywhere'},
  { label: 'To Do List', url: '/toDoList'},
];
function RTMenuDrawer({ userContext }) {
  const history = useHistory();
  const classes = useStyles();
  const context = useContext(AuthContext);
  const onClose = () => { }
  const handleLinkClick = (url) => {
    history.push(url);
    showNav(false)
  }
  const { openNav, showNav } = useNavigation();
  return (
    <SwipeableDrawer
      anchor="left"
      open={openNav}
      onClose={() => { showNav(false) }}
      onOpen={() => { showNav(true) }}
    >
      <div
        className={clsx(classes.list)}
        role="presentation"
        onClick={onClose}
        onKeyDown={onClose}
      >
        <div className={classes.header}>
          <div>
            <img alt="" src="data:image/png;base64,"
              className={classes.avator} />
            <div>
              <div className={classes.avatorName}>{"Smith Anderson"}</div>
              <div className={classes.role}>Test Role</div>
            </div>
          </div>
          <button
            className={`nebula-icons neb-logout ${classes.logOut}`}
            onClick={(e) => {
              context.logout();
            }}
          >
          </button>
        </div>
        <Divider />
        <List>
          {MENU_LIST.map((item, index) => (
            <ListItem button key={item.label} onClick={() => handleLinkClick(item.url)}>
              <ListItemIcon style={{ minWidth: "30px" }} className={item.icon} />
              <ListItemText primary={item.label} className={clsx(classes.listItem)} />
            </ListItem>
          ))}
        </List>
        <div style={{ position: "absolute", bottom: "10px", right: "10px" }}>
          <RamcoLogo />
        </div>
      </div>
    </SwipeableDrawer>
  );
}

const mapStateToProps = (
  {
    workrequest: {
      userContext
    }
  }
) => {
  return {
    userContext
  };
};

export default connect(mapStateToProps)(RTMenuDrawer);
